		output?: string,
		options?: TOptions,
	): TSESLint.InvalidTestCase<TMessageIds, TOptions> {
		return {
			code,
			errors,
			...(output !== null && output !== undefined && output !== ""
				? { output }
				: {}),
			...(options !== null && options !== undefined ? { options } : {}),
		};
	}

	/**
	 * –°–æ–∑–¥–∞–µ—Ç RuleTester –¥–ª—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –≤ —Ç–µ—Å—Ç–∞—Ö
	 *
