		// WHY: Ensure mathematical properties are preserved during parsing
		// FORMAT THEOREM: ‚àÄ code ‚àà TestCases: parseable(code) ‚Üí ast_correct(code)
		const parser = await import("@typescript-eslint/parser");

		this.ruleTester = new RuleTester({
			languageOptions: {
				parser: (parser.default ?? parser) as any,
				parserOptions: {
					ecmaVersion: "latest" as const,
					sourceType: "module" as const,
					project: path.resolve(process.cwd(), "tsconfig.json"),
					tsconfigRootDir: path.resolve(__dirname, "../.."),
				},
			},
		});

		return this.ruleTester;
	}

	/**
	 * –í—ã–ø–æ–ª–Ω—è–µ—Ç –º–∞—Ç–µ–º–∞—Ç–∏—á–µ—Å–∫–∏ –≤–µ—Ä–∏—Ñ–∏—Ü–∏—Ä—É–µ–º–æ–µ —Ç–µ—Å—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –ø—Ä–∞–≤–∏–ª–∞
	 *
