	it("should satisfy idempotency: running rule twice produces same result", () => {
		// CHANGE: Test idempotency property
		// WHY: Rule should be deterministic
		// INVARIANT: ‚àÄ code: result(rule(code)) = result(rule(rule(code)))
		const testCode = `import { formatString } from './helpr';`;

