// eslint.config.mjs
import js from "@eslint/js";
import eslintComments from "@eslint-community/eslint-plugin-eslint-comments";
import suggestMembers from "@ton-ai-core/eslint-plugin-suggest-members-aliased";
import importX from "eslint-plugin-import-x";
import jestPlugin from "eslint-plugin-jest";
import jsonc from "eslint-plugin-jsonc";
import promisePlugin from "eslint-plugin-promise";
import simpleImportSort from "eslint-plugin-simple-import-sort";
import sql from "eslint-plugin-sql";
import sqlTemplate from "eslint-plugin-sql-template";
import typeormTS from "eslint-plugin-typeorm-typescript";
import unusedImports from "eslint-plugin-unused-imports";
import yml from "eslint-plugin-yml";
import globals from "globals";
import tseslint from "typescript-eslint";
import eslintPlugin from 'eslint-plugin-eslint-plugin';

export default tseslint.config(
	// –ò–≥–Ω–æ—Ä—ã
	{
		ignores: [
			"dist/**",
			"build/**",
			".api",
			"*.json",
			"*.yml",
			"*.yaml",
			"*.config.js",
			"*.config.ts",
			"reports/**",
		],
	},

	// –û—Å–Ω–æ–≤–Ω—ã–µ TS-—Ñ–∞–π–ª—ã
	{
		files: ["**/*.{ts,tsx}"],
		extends: [
			eslintPlugin.configs.recommended,
			js.configs.recommended,
			...tseslint.configs.recommended,
			...tseslint.configs.recommendedTypeChecked,
		],
		plugins: {
			"@eslint-community/eslint-comments": eslintComments,
			jest: jestPlugin,
			"@ton-ai-core/eslint-plugin-suggest-members": suggestMembers,
			"typeorm-typescript": typeormTS,
			sql,
			"sql-template": sqlTemplate,
			"import-x": importX,
			"simple-import-sort": simpleImportSort,
			"unused-imports": unusedImports,
			promise: promisePlugin,
			jsonc,
			yml,
		},
		languageOptions: {
			ecmaVersion: "latest",
			sourceType: "module",
			globals: { ...globals.node, ...globals.browser },
			parserOptions: {
				tsconfigRootDir: import.meta.dirname,
				projectService: true,
			},
		},
		settings: {
			// –†–µ–∑–æ–ª–≤–µ—Ä—ã –¥–ª—è import-x: TS-–∞–ª–∏–∞—Å—ã + node:* core-–º–æ–¥—É–ª–∏
			"import-x/resolver": {
				typescript: {
					alwaysTryTypes: true,
					project: "./tsconfig.json",
				},
				node: {
					extensions: [".ts", ".tsx", ".d.ts", ".js", ".jsx", ".json", ".node"],
					preferBuiltins: true,
				},
			},
			"import-x/core-modules": [
				"node:fs",
				"node:path",
				"node:url",
				"node:crypto",
				"node:os",
				"node:stream",
				"node:http",
				"node:https",
				"node:buffer",
				"node:util",
			],
		},
		rules: {
			'eslint-plugin/require-meta-docs-description': 'error',

			// –ú–µ—Ç—Ä–∏–∫–∏/—Å–ª–æ–∂–Ω–æ—Å—Ç—å
			complexity: ["error", 8],
			"max-lines-per-function": [
				"error",
				{ max: 50, skipBlankLines: true, skipComments: true },
			],
			"max-params": ["error", 5],
			"max-depth": ["error", 4],
			"max-lines": [
				"error",
				{ max: 300, skipBlankLines: true, skipComments: true },
			],

			// –ê—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä–∞/–∏–º–ø–æ—Ä—Ç—ã
			"import-x/no-cycle": ["error", { maxDepth: 10 }],
			"no-restricted-imports": [
				"error",
				{
					patterns: ["src/domain/**"],
					paths: [
						{
							name: "src/domain",
							message:
								"Domain layer must not be imported by UI layer. Use domain/public-api instead.",
						},
					],
				},
			],

			// TS —Å—Ç—Ä–æ–≥–∏–µ –ø—Ä–∞–≤–∏–ª–∞
			"@typescript-eslint/no-unused-vars": [
				"error",
				{
					argsIgnorePattern: "^_",
					varsIgnorePattern: "^_",
					caughtErrorsIgnorePattern: "^_",
				},
			],
			"@typescript-eslint/no-explicit-any": "error",
			// TS-–≤–µ—Ä—Å–∏—è —Å —Ä–∞–∑—Ä–µ—à–µ–Ω–∏–µ–º –∏–¥–∏–æ–º
			"no-unused-expressions": "off",
			"@typescript-eslint/no-unused-expressions": [
				"error",
				{ allowShortCircuit: true, allowTernary: true },
			],
			"@typescript-eslint/explicit-function-return-type": "error",
			"@typescript-eslint/explicit-module-boundary-types": "error",
			"@typescript-eslint/no-unsafe-assignment": "error",
			"@typescript-eslint/no-unsafe-member-access": "error",
			"@typescript-eslint/no-unsafe-call": "error",
			"@typescript-eslint/no-unsafe-return": "error",
			"@typescript-eslint/switch-exhaustiveness-check": "error",
			"@typescript-eslint/no-floating-promises": "error",
			"@typescript-eslint/await-thenable": "error",
			"@typescript-eslint/no-misused-promises": "error",
