import type { TSESTree } from "@typescript-eslint/utils";
import { ESLintUtils } from "@typescript-eslint/utils";

const createRule = ESLintUtils.RuleCreator(
	(name) =>
		`https://github.com/ton-ai-core/eslint-plugin-suggest-members#${name}`,
);

export const suggestMembersRule = createRule({
	name: "suggest-members",
	meta: {
		type: "problem",
		docs: {
			description:
				"enforce correct member names when accessing non-existent properties",
		},
		messages: {
			memberNotFound:
				'Property "{{name}}" does not exist. Did you mean:\n{{suggestions}}',
		},
		schema: [],
	},
	defaultOptions: [],
	create(context) {
		return {
			MemberExpression(node: TSESTree.MemberExpression): void {
				// Simple implementation for now
				if (node.computed || node.optional) return;
				if (!node.property || node.property.type !== "Identifier") return;

				// For now, just report all member accesses as test
				context.report({
					node: node.property,
					messageId: "memberNotFound",
					data: {
						name: node.property.name,
						suggestions: "  - test suggestion",
					},
				});
			},
		};
	},
});

