			const results = await eslint.lintText(code, { filePath });

			expect(results).toHaveLength(1);
			const messages = results[0]?.messages || [];

			// File should have errors from multiple rules
			expect(messages.length).toBeGreaterThan(0);

			// Group errors by rule
			const errorsByRule = messages.reduce(
				(acc, msg) => {
					const rule = msg.ruleId ?? "unknown";
					acc[rule] = (acc[rule] ?? 0) + 1;
					return acc;
				},
				{} as Record<string, number>,
			);

			// At least suggest-module-paths should fire (most reliable)
			expect(
				errorsByRule["suggest-members/suggest-module-paths"],
			).toBeGreaterThan(0);
		});
	});

	describe("Performance requirements", () => {
		it("should lint test files in reasonable time", async () => {
			// CHANGE: Test performance on real files
			// WHY: Ensure rules don't cause significant slowdown
			const filePath = resolve(testProjectDir, "src", "test-all-rules.ts");
			const code = await readFile(filePath, "utf-8");

			const eslint = createTestESLint();

			const start = performance.now();
			await eslint.lintText(code, { filePath });
			const duration = performance.now() - start;

			// Should complete in reasonable time (< 5 seconds for a small file)
			expect(duration).toBeLessThan(5000);
		});
	});
});

