// CHANGE: Core message formatting functions
// WHY: Pure string formatting without side effects
// PURITY: CORE
// REF: system-promt.md - —á–∏—Å—Ç—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏ —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏—è

import type { SuggestionWithScore } from "../types/domain-types.js";

// CHANGE: Changed to multi-line bullet list format with limit of 5 suggestions
// WHY: Improved readability and consistency with TypeScript error format
// QUOTE: "–û—à–∏–±–∫–∏ –¥–æ–ª–∂–Ω—ã –±—ã—Ç—å –≤ —Ç–∞–∫–æ–º —Å—Ç–∏–ª–µ: [multi-line format]"
// QUOTE: "–º—ã –≤—ã–≤–æ–¥–∏–º –¥–æ 5 —Å–∞–º—ã—Ö –≤–µ—Ä–æ—è—Ç–Ω—ã—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤"

const MAX_SUGGESTIONS = 5;

/**
 * Formats suggestion list into readable string
 *
 * @param suggestions - Scored suggestions
 * @returns Formatted message
 *
 * @pure true
 * @purity CORE
 * @effect None
 * @complexity O(n) where n = min(|suggestions|, MAX_SUGGESTIONS)
 * @throws Never
 */
export function formatSuggestionMessage(
	suggestions: readonly SuggestionWithScore[],
): string {
	if (suggestions.length === 0) {
		return "No similar suggestions found.";
	}

	const topSuggestions = suggestions.slice(0, MAX_SUGGESTIONS);
	const bulletList = topSuggestions.map((s) => `  - ${s.name}`).join("\n");

	return `Did you mean:\n${bulletList}`;
}

/**
 * Common suggestion list formatter with multi-line bullet list
 *
 * @purity CORE
 * @complexity O(n) where n = min(|suggestions|, MAX_SUGGESTIONS)
 */
const formatSuggestionList = (
	suggestions: readonly SuggestionWithScore[],
): string => {
	const topSuggestions = suggestions.slice(0, MAX_SUGGESTIONS);
	return topSuggestions.map((s) => `  - ${s.name}`).join("\n");
};

/**
 * Formats member validation message with full context
 *
 * @param propertyName - Property name that was not found
 * @param typeName - Type name where property was accessed (optional)
 * @param suggestions - Scored suggestions
 * @returns Formatted message
 *
 * @pure true
 * @purity CORE
 * @effect None
 * @complexity O(n) where n = min(|suggestions|, MAX_SUGGESTIONS)
 * @throws Never
 */
export function formatMemberMessage(
	propertyName: string,
	typeName: string | undefined,
	suggestions: readonly SuggestionWithScore[],
): string {
	const typeContext = typeName !== undefined ? ` on type "${typeName}"` : "";
	const prefix = `Property "${propertyName}" does not exist${typeContext}. Did you mean`;
	return `${prefix}:\n${formatSuggestionList(suggestions)}`;
}

/**
 * Formats import validation message with full context
 *
 * @param importName - Import name that was not found
 * @param modulePath - Module path being imported from
 * @param suggestions - Scored suggestions
 * @returns Formatted message
 *
 * @pure true
 * @purity CORE
 * @effect None
 * @complexity O(n) where n = min(|suggestions|, MAX_SUGGESTIONS)
 * @throws Never
 */
export function formatImportMessage(
	importName: string,
	modulePath: string,
	suggestions: readonly SuggestionWithScore[],
): string {
	const prefix = `Cannot find export "${importName}" in module "${modulePath}". Did you mean`;
	return `${prefix}:\n${formatSuggestionList(suggestions)}`;
}

/**
 * Formats module path validation message with full context
 *
 * @param requestedPath - Module path that was not found
 * @param suggestions - Scored suggestions
 * @returns Formatted message
 *
 * @pure true
 * @purity CORE
 * @effect None
 * @complexity O(n) where n = min(|suggestions|, MAX_SUGGESTIONS)
 * @throws Never
 */
export function formatModuleMessage(
	requestedPath: string,
	suggestions: readonly SuggestionWithScore[],
): string {
	const prefix = `Cannot find module "${requestedPath}". Did you mean`;
	return `${prefix}:\n${formatSuggestionList(suggestions)}`;
}

// CHANGE: Effect-based export validation with mathematical guarantees
// WHY: Pure functional composition separating CORE logic from SHELL effects
// PURITY: SHELL (CORE + SHELL composition)
// REF: FUNCTIONAL_ARCHITECTURE.md - SHELL layer
// INVARIANT: ‚àÄ export: exists(export) ‚à® suggest(similar_exports)

import type { Effect } from "effect";
import { match } from "ts-pattern";

import { formatImportMessage } from "../../core/formatting/messages.js";
import type { ExportValidationResult } from "../../core/types/validation-types.js";
import {
	makeExportNotFoundResult,
	makeValidExportResult,
} from "../../core/types/validation-types.js";
import type { TypeScriptServiceError } from "../effects/errors.js";
import type { TypeScriptCompilerServiceTag } from "../services/typescript-compiler-effect.js";
import { baseValidationEffect } from "./validation-base-effect.js";

/**
 * Validates export access with Effect-based composition
 *
 * @param node - Import specifier node
 * @param exportName - Name being imported
 * @param modulePath - Module path
 * @returns Effect with validation result
 *
 * @purity SHELL
 * @effect Effect<ExportValidationResult, TypeScriptServiceError, TypeScriptCompilerServiceTag>
 * @invariant Result is Valid | ExportNotFound (exhaustive)
 * @complexity O(n log n) where n = |available_exports|
 * @throws Never - –≤—Å–µ –æ—à–∏–±–∫–∏ —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω—ã –≤ Effect
 *
 * FORMAT THEOREM: ‚àÄnode,name,path: validateExport(node,name,path) ‚Üí Effect<ValidationResult>
 */
export const validateExportAccessEffect = (
	node: object, // ESLint ImportSpecifier
	exportName: string,
	modulePath: string,
): Effect.Effect<
	ExportValidationResult,
	TypeScriptServiceError,
	TypeScriptCompilerServiceTag
> => {
	// CHANGE: Use base validation with export-specific configuration
	// WHY: Eliminate code duplication with import validation
	const config = {
		makeValidResult: makeValidExportResult,
		makeInvalidResult: makeExportNotFoundResult,
		isValidCandidate: isValidExportCandidate,
	};

	return baseValidationEffect(node, exportName, modulePath, config);
};

/**
 * Validates if candidate is suitable for export suggestions
 *
 * @param candidate - Export candidate
 * @param userInput - User's input
 * @returns true if candidate is valid
 *
 * @pure true
 * @purity CORE
 * @effect None
 * @invariant ‚àÄcandidate,input: isValid(candidate,input) ‚àà {true, false}
 * @complexity O(1)
 * @throws Never
 */
export const isValidExportCandidate = (
	candidate: string,
	userInput: string,
): boolean => {
	// CHANGE: Skip private exports
	// WHY: Private exports are less likely to be intended
	if (candidate.startsWith("_")) return false;

	// CHANGE: Skip exact matches
	// WHY: Exact matches would have been found already
	if (candidate === userInput) return false;

	// CHANGE: Skip empty names
	// WHY: Empty names are not valid exports
	if (candidate.length === 0) return false;

	// CHANGE: Skip internal TypeScript symbols
	// WHY: Internal symbols are not user-facing
	if (candidate.startsWith("__")) return false;

	// CHANGE: Skip default export in named import context
	// WHY: Default exports have different import syntax
	if (candidate === "default") return false;

	return true;
};

/**
 * Formats export validation result into user message
 *
 * @param result - Validation result
 * @returns Formatted message
 *
 * @pure true
 * @purity CORE
 * @effect None
 * @invariant ‚àÄresult: format(result) ‚àà String
 * @complexity O(n) where n = |suggestions|
 * @throws Never
 */
export const formatExportValidationMessage = (
	result: ExportValidationResult,
): string =>
	match(result)
		.with({ _tag: "Valid" }, () => "")
		.with({ _tag: "ExportNotFound" }, (invalid) => {
			const { exportName, modulePath, suggestions } = invalid;

			if (suggestions.length === 0) {
				return `Cannot find export "${exportName}" in module "${modulePath}".`;
			}

			return formatImportMessage(exportName, modulePath, suggestions);
		})
		.exhaustive();

// CHANGE: –î–æ–º–µ–Ω–Ω—ã–µ —Ç–∏–ø—ã —Å –±—Ä–µ–Ω–¥–∏—Ä–æ–≤–∞–Ω–∏–µ–º –¥–ª—è type safety
// WHY: –ú–∞—Ç–µ–º–∞—Ç–∏—á–µ—Å–∫–∞—è —Å—Ç—Ä–æ–≥–æ—Å—Ç—å —á–µ—Ä–µ–∑ —Å–∏—Å—Ç–µ–º—É —Ç–∏–ø–æ–≤
// PURITY: CORE
// REF: CLAUDE.md - —Å—Ç—Ä–æ–≥–∞—è —Ç–∏–ø–∏–∑–∞—Ü–∏—è –±–µ–∑ any/unknown

/**
 * Similarity score bounded in mathematical range [0, 1]
 *
 * @pure true
 * @purity CORE
 * @invariant 0 ‚â§ value ‚â§ 1
 *
 * FORMAT THEOREM: SimilarityScore ‚àà [0, 1] ‚äÇ ‚Ñù
 */
export type SimilarityScore = number & { readonly __brand: "SimilarityScore" };

/**
 * Creates validated similarity score
 *
 * @pure true
 * @purity CORE
 * @invariant Output is clamped to [0, 1]
 * @complexity O(1)
 * @throws Never
 */
export const makeSimilarityScore = (value: number): SimilarityScore => {
	const clamped = value < 0 ? 0 : value > 1 ? 1 : value;
	return clamped as SimilarityScore;
};

/**
 * Normalized string (lowercase, no separators)
 *
 * @pure true
 * @purity CORE
 * @invariant /^[a-z0-9]*$/.test(value)
 */
export type NormalizedString = string & {
	readonly __brand: "NormalizedString";
};

/**
 * Token from identifier splitting
 *
 * @pure true
 * @purity CORE
 * @invariant length > 0 ‚àß value === value.toLowerCase()
 */
export type Token = string & { readonly __brand: "Token" };

/**
 * Suggestion with similarity score
 *
 * @pure true
 * @purity CORE
 * @invariant score ‚àà [0, 1]
 */
export interface SuggestionWithScore {
	readonly name: string;
	readonly score: SimilarityScore;
}

/**
 * Minimum score threshold
 *
 * @pure true
 * @purity CORE
 */
