	const hasExport = (
		modulePath: string,
		exportName: string,
	): Effect.Effect<boolean, TypeScriptServiceError> =>
		Effect.gen(function* (_) {
			const exports = yield* _(getExportsOfModule(modulePath));
			return exports.includes(exportName);
		});

	const getExportTypeSignature = (
		modulePath: string,
		exportName: string,
	): Effect.Effect<string | undefined, TypeScriptServiceError> => {
		// CHANGE: Get type signature for exports
		// WHY: Provide type context in error messages

		if (checker === null || program === null) {
			// If no type checker, just return undefined (no signature)
			return Effect.succeed(undefined);
		}

		const sourceFile = program.getSourceFile(modulePath);
		if (sourceFile === undefined) {
			return Effect.succeed(undefined);
		}

		const moduleSymbol = checker.getSymbolAtLocation(sourceFile);
		if (moduleSymbol === undefined) {
			return Effect.succeed(undefined);
		}

		const exports = checker.getExportsOfModule(moduleSymbol);
		const targetSymbol = exports.find((exp) => exp.name === exportName);

		if (targetSymbol === undefined) {
			return Effect.succeed(undefined);
		}

		// Get type of the symbol
		const symbolType = checker.getTypeOfSymbolAtLocation(
			targetSymbol,
			sourceFile,
		);

		// Format type as string
		const signature = checker.typeToString(
			symbolType,
			undefined,
			ts.TypeFormatFlags.NoTruncation |
				ts.TypeFormatFlags.UseAliasDefinedOutsideCurrentScope,
		);

		return Effect.succeed(signature);
	};

	return {
		getSymbolAtLocation,
		getTypeAtLocation,
		getPropertiesOfType,
		getExportsOfModule,
		hasExport,
		getExportTypeSignature,
	};
};

