/**
 * Filesystem-based export validation effect
 * CHANGE: Create filesystem fallback for export validation
 * WHY: TypeScript service may fail in ESLint context
 * PURITY: SHELL - filesystem operations with side effects
 */

import { Effect } from "effect";
import { readFileSync } from "fs";
import { dirname, resolve } from "path";

import type { ExportValidationResult } from "../../core/types/validation-types.js";
import {
	makeExportNotFoundResult,
	makeValidExportResult,
} from "../../core/types/validation-types.js";
import { createSuggestionsWithScore } from "../../core/utils/suggestions.js";
import type { FilesystemService } from "../services/filesystem.js";
import { parseExportsFromContent } from "./export-parser.js";

/**
 * Creates filesystem-based export validation effect
 * @param filesystemService - Filesystem service for file operations
 * @returns Effect that validates exports using filesystem
 * @complexity O(1) - single file read operation
 * @purity SHELL - reads files from filesystem
 */
export const createFilesystemExportValidationEffect =
	(filesystemService: FilesystemService) =>
	(
		importName: string,
		modulePath: string,
		contextFilePath: string,
	): Effect.Effect<ExportValidationResult, never, never> =>
		Effect.succeed(
			validateExportInModule(
				importName,
				modulePath,
				contextFilePath,
				filesystemService,
			),
		);

// CHANGE: Remove unused function - using parseExportsFromContent directly
// WHY: Avoid code duplication

/**
 * Validates export in a specific module
 * @param importName - Name to validate
 * @param modulePath - Module path
 * @param contextFilePath - Context file path
 * @param filesystemService - Filesystem service
 * @returns Validation result
 * @purity SHELL - filesystem operations
 */
const validateExportInModule = (
	importName: string,
	modulePath: string,
	contextFilePath: string,
	_filesystemService: FilesystemService,
): ExportValidationResult => {
	try {
		// CHANGE: Only validate local modules (relative paths)
		// WHY: This fallback is only for local modules
		if (!modulePath.startsWith("./") && !modulePath.startsWith("../")) {
			return makeValidExportResult();
		}

		// CHANGE: Resolve module path relative to context file
		// WHY: Need absolute path for filesystem operations
		const contextDir = dirname(contextFilePath);
		const resolvedPath = resolve(contextDir, modulePath);

		// CHANGE: Try different extensions
		// WHY: Module path may not include extension
		const possiblePaths = [
			resolvedPath,
			resolvedPath + ".ts",
			resolvedPath + ".js",
			resolvedPath + "/index.ts",
			resolvedPath + "/index.js",
		];

		return validateInPossiblePaths(importName, modulePath, possiblePaths);
	} catch {
		// CHANGE: Return valid on any error
		// WHY: Don't break linting if validation fails
		return makeValidExportResult();
	}
};

/**
 * Validates export in possible file paths
 */
const validateInPossiblePaths = (
	importName: string,
	modulePath: string,
	possiblePaths: string[],
): ExportValidationResult => {
	for (const filePath of possiblePaths) {
		try {
			// CHANGE: Read file content and extract exports
			// WHY: Need to check what the module actually exports
			const content = readFileSync(filePath, "utf-8");
			const exports = parseExportsFromContent(content);

			// CHANGE: Check if import name exists in exports
			// WHY: This is the main validation logic
			if (exports.includes(importName)) {
				return makeValidExportResult();
			}

			// CHANGE: Find similar export names
			// WHY: Provide helpful suggestions
			const suggestionsWithScore = createSuggestionsWithScore(
				importName,
				exports,
			);
			if (suggestionsWithScore.length > 0) {
				return makeExportNotFoundResult(
					importName,
					modulePath,
					suggestionsWithScore,
					{},
				);
			}

			// CHANGE: Return not found without suggestions
			// WHY: Export doesn't exist and no similar names found
			return makeExportNotFoundResult(importName, modulePath, [], {});
		} catch {
			// CHANGE: Continue to next path on read error
			// WHY: File might exist but be unreadable
			continue;
		}
	}

	// CHANGE: Return valid if no file found
	// WHY: Don't report errors for missing files (might be external modules)
	return makeValidExportResult();
};

// CHANGE: Base validation effect with shared logic
// WHY: Eliminate code duplication between export and import validation
// PURITY: SHELL (shared validation logic)
// REF: FUNCTIONAL_ARCHITECTURE.md - DRY principle

import { Effect, pipe } from "effect";

import {
	findSimilarCandidatesEffect,
	isTypeOnlyImport,
	shouldSkipIdentifier,
} from "../../core/index.js";
import type { SuggestionWithScore } from "../../core/types/domain-types.js";
import type { TypeScriptServiceError } from "../effects/errors.js";
import { TypeScriptCompilerServiceTag } from "../services/typescript-compiler-effect.js";

/**
 * Base validation configuration for different validation types
 *
 * @purity SHELL
 */
export interface BaseValidationConfig<TResult> {
	readonly makeValidResult: () => TResult;
	readonly makeInvalidResult: (
		name: string,
		modulePath: string,
		suggestions: readonly SuggestionWithScore[],
		node: object,
	) => TResult;
	readonly isValidCandidate: (candidate: string, userInput: string) => boolean;
}

/**
 * Base validation effect with shared logic
 *
 * @param node - Import specifier node
 * @param name - Name being validated
 * @param modulePath - Module path
 * @param config - Validation configuration
 * @returns Effect with validation result
 *
 * @purity SHELL
 * @effect Effect<TResult, TypeScriptServiceError, TypeScriptCompilerServiceTag>
 * @invariant Result is Valid | Invalid (exhaustive)
 * @complexity O(n log n) where n = |available_exports|
 * @throws Never - –≤—Å–µ –æ—à–∏–±–∫–∏ —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω—ã –≤ Effect
 */
export const baseValidationEffect = <TResult>(
	node: object,
	name: string,
	modulePath: string,
	config: BaseValidationConfig<TResult>,
): Effect.Effect<
	TResult,
	TypeScriptServiceError,
	TypeScriptCompilerServiceTag
> =>
	pipe(
		Effect.gen(function* (_) {
			// CHANGE: Skip type-only imports using pure predicate
			// WHY: Type-only imports have different validation rules
			// PURITY: CORE
			if (isTypeOnlyImport(node)) {
				return config.makeValidResult();
			}

			// CHANGE: Skip identifiers that should not be validated
			// WHY: Some identifiers are special cases
			// PURITY: CORE
			if (shouldSkipIdentifier(name)) {
				return config.makeValidResult();
			}

			// CHANGE: Get TypeScript service from context
			// WHY: Dependency injection pattern
			// PURITY: SHELL
			const tsService = yield* _(TypeScriptCompilerServiceTag);

			// CHANGE: Get all exports from module
			// WHY: Need available exports for validation and suggestions
			// PURITY: SHELL
			const availableExports = yield* _(
				tsService.getExportsOfModule(modulePath),
			);

			// CHANGE: Debug logging for troubleshooting
			// WHY: Need to understand what exports are found

			// CHANGE: Check if export exists
			// WHY: Valid exports don't need suggestions
			// PURITY: CORE
			if (availableExports.includes(name)) {
				return config.makeValidResult();
			}

			// CHANGE: Filter valid candidates using pure predicate
			// WHY: Remove invalid suggestions before similarity matching
			// PURITY: CORE
			const validCandidates = availableExports.filter((candidate) =>
				config.isValidCandidate(candidate, name),
			);

			// CHANGE: Find similar candidates using pure Effect
			// WHY: Separate similarity computation from validation logic
			// PURITY: CORE
			const suggestions = yield* _(
				findSimilarCandidatesEffect(name, validCandidates),
			);

			// CHANGE: Return typed validation result
			// WHY: Type-safe error handling without exceptions
			// PURITY: CORE
			return config.makeInvalidResult(name, modulePath, suggestions, node);
		}),
	);

